function ref = avg_ref3d_baby109_noOuter(matrix);% use only top124 channels, the rest is zero because no eye channels...for trial = 1:size(matrix,3);     matrix4ref = matrix(1:108,:,trial); % calculate mean for each time stamp = avg, which will be the referencemean_col = mean(matrix4ref', 2);for i=1:length(matrix4ref(1,:));	ref2(:,i) = matrix4ref(:,i)-mean_col(i);end;% now put everything together, add zeros for eye channels, and put avg% refernce at the referencematrix(1:108,:,trial)=ref2; matrix(109,:,trial) = mean_col;ref(:,:,trial) = matrix(:,:,trial); endend